mapper.update:

	  update orders
	     set quantity = #{quantity},
          ORDERTYPE= 2,
          optionnum = #{optionNum}
	     where ordernum= #{ordernum}


update prodlike
   set PRODCOMMENT = #{prodComment}
   where likenum= #{likeNum}

update.foreach:
UPDATE prodoption 
SET quantity = quantity -#{item.quantity}
WHERE prodNum= #{item.prodNum}
and optionNum=#{item.optionNum}

